SET NOCOUNT ON; SET DEADLOCK_PRIORITY LOW;
DECLARE @DatabaseName [SYSNAME] ,@strSQL [NVARCHAR](4000);
CREATE TABLE #tblOrphanUsers ( [UserName] [SYSNAME] NULL ,[UserID] [VARBINARY](255) NULL ,[CreateDate] [DATETIME] NULL ,[UpdateDate] [DATETIME] NULL );
DECLARE @tblDatabaseOrphanUsers TABLE ( [DatabaseName] [SYSNAME] NULL ,[UserName] [SYSNAME] NULL ,[UserID] [VARBINARY](255) NULL ,[CreateDate] [DATETIME] NULL ,[UpdateDate] [DATETIME] NULL );
SELECT @DatabaseName = MIN(tSD.[name]) FROM [master].[dbo].[sysdatabases] tSD WHERE tSD.[name] NOT IN ( N'tempdb' ) AND DatabasePropertyEx(tSD.[name], N'Status') IN ( N'ONLINE' );
WHILE (@DatabaseName IS NOT NULL) BEGIN SET @strSQL = N'USE ' + QuoteName(@DatabaseName, N']') + N';SELECT' + N' tSU.[name]' + N' ,tSU.[sid]' + N' ,tSU.[createdate]' + N' ,tSU.[updatedate]' + N'FROM' + N' [dbo].[sysusers] tSU' + N' LEFT OUTER JOIN [master].[dbo].[syslogins] tSL ON' + N' tSL.[sid] = tSU.[sid]' + N'WHERE' + N' tSU.[sid] NOT IN (0x0)' + N' AND tSU.[issqluser] = 1' + N' AND tSL.[sid] IS NULL;';
DELETE FROM #tblOrphanUsers;
INSERT #tblOrphanUsers ( [UserName] ,[UserID] ,[CreateDate] ,[UpdateDate] ) EXECUTE [sp_executesql] @strSQL;
INSERT @tblDatabaseOrphanUsers ( [DatabaseName] ,[UserName] ,[UserID] ,[CreateDate] ,[UpdateDate] ) SELECT @DatabaseName ,t.[UserName] ,t.[UserID] ,t.[CreateDate] ,t.[UpdateDate] FROM #tblOrphanUsers t;
SELECT @DatabaseName = MIN(tSD.[name]) FROM [master].[dbo].[sysdatabases] tSD WHERE tSD.[name] NOT IN ( N'tempdb' ) AND DatabasePropertyEx(tSD.[name], N'Status') IN ( N'ONLINE' ) AND tSD.[name] > @DatabaseName; END
SELECT t.[DatabaseName] ,t.[UserName] ,t.[UserID] ,t.[CreateDate] ,t.[UpdateDate] FROM @tblDatabaseOrphanUsers t;

SET NOCOUNT ON; SET DEADLOCK_PRIORITY LOW;
DECLARE @DatabaseName [SYSNAME] ,@strSQL [NVARCHAR](4000);
CREATE TABLE #tblOrphanUsers ( [UserName] [SYSNAME] NULL ,[UserID] [VARBINARY](255) NULL ,[CreateDate] [DATETIME] NULL ,[UpdateDate] [DATETIME] NULL );
DECLARE @tblDatabaseOrphanUsers TABLE ( [DatabaseName] [SYSNAME] NULL ,[UserName] [SYSNAME] NULL ,[UserID] [VARBINARY](255) NULL ,[CreateDate] [DATETIME] NULL ,[UpdateDate] [DATETIME] NULL );
SELECT @DatabaseName = MIN(tSD.[name]) FROM [master].[sys].[sysdatabases] tSD WHERE tSD.[name] NOT IN ( N'tempdb' ) AND DatabasePropertyEx(tSD.[name], N'Status') IN ( N'ONLINE' );
WHILE (@DatabaseName IS NOT NULL) BEGIN SET @strSQL = N'USE ' + QuoteName(@DatabaseName, N']') + N'; SELECT tSU.[name] ,tSU.[sid] ,tSU.[createdate] ,tSU.[updatedate] FROM [sys].[sysusers] tSU LEFT OUTER JOIN [master].[sys].[syslogins] tSL ON tSL.[sid] = tSU.[sid] WHERE tSU.[sid] NOT IN (0x0) AND tSU.[issqluser] = 1 AND tSL.[sid] IS NULL; ';
DELETE FROM #tblOrphanUsers;
BEGIN TRY INSERT #tblOrphanUsers ( [UserName] ,[UserID] ,[CreateDate] ,[UpdateDate] ) EXECUTE [sp_executesql] @strSQL; END TRY BEGIN CATCH INSERT #tblOrphanUsers ( [UserName] ,[UserID] ,[CreateDate] ,[UpdateDate] ) VALUES ( N'?' ,-1 ,N'19000101' ,N'19000101' ); END CATCH
INSERT @tblDatabaseOrphanUsers ( [DatabaseName] ,[UserName] ,[UserID] ,[CreateDate] ,[UpdateDate] ) SELECT @DatabaseName ,t.[UserName] ,t.[UserID] ,t.[CreateDate] ,t.[UpdateDate] FROM #tblOrphanUsers t;
SELECT @DatabaseName = MIN(tSD.[name]) FROM [master].[sys].[sysdatabases] tSD WHERE tSD.[name] NOT IN ( N'tempdb' ) AND DatabasePropertyEx(tSD.[name], N'Status') IN ( N'ONLINE' ) AND tSD.[name] > @DatabaseName; END
SELECT t.[DatabaseName] ,t.[UserName] ,t.[UserID] ,t.[CreateDate] ,t.[UpdateDate] FROM @tblDatabaseOrphanUsers t;
