sql-select.sql-select-text:
SET NOCOUNT ON; SET DEADLOCK_PRIORITY LOW;
CREATE TABLE #tDBCCDBInfo ( [ParentObject] [NVARCHAR](128) ,[Object] [NVARCHAR](128) ,[Field] [NVARCHAR](128) ,[Value] [NVARCHAR](128) );
INSERT #tDBCCDBInfo ( [ParentObject] ,[Object] ,[Field] ,[Value] ) EXEC (N'DBCC DBInfo() WITH TableResults, NO_INFOMSGS;');
SELECT t.[ParentObject] AS [ParentObject] ,t.[Object] AS [Object] ,t.[Field] AS [Field] ,t.[Value] AS [Value] ,CASE WHEN ((t.[Field] = N'dbi_dbccLastKnownGood') AND (t.[Value] = N'1900-01-01 00:00:00.000')) THEN 1 WHEN (t.[Field] = N'dbi_dbccLastKnownGood') AND CONVERT([DATETIME], t.[Value], 121) < DATEADD(dd, -1 * ISNULL(@intDaysDBCCLastKnownGoodThreshold, 1), CURRENT_TIMESTAMP) THEN 1 ELSE 0 END AS [IsIssue] ,CASE WHEN ((t.[Field] = N'dbi_dbccLastKnownGood') AND (t.[Value] = N'1900-01-01 00:00:00.000')) THEN CONVERT([NVARCHAR](256), N'Never had a successful DBCC CHECKDB') WHEN (t.[Field] = N'dbi_dbccLastKnownGood') THEN CONVERT([NVARCHAR](256), N'Last time had a successful DBCC CHECKDB') WHEN (t.[Field] = N'dbi_modDate') THEN CONVERT([NVARCHAR](256), N'The date the database was initially created') WHEN (t.[Field] = N'dbi_crdate') THEN CONVERT([NVARCHAR](256), N'The date the database was created on the instance') ELSE NULL END AS [CommentMessage] FROM #tDBCCDBInfo t WHERE t.[Field] IN ( N'dbi_LastLogBackupTime' ,N'dbi_cmptlevel' ,N'dbi_crdate' ,N'dbi_createVersion' ,N'dbi_dbccLastKnownGood' ,N'dbi_modDate' ,N'dbi_version' ) ORDER BY t.[ParentObject] ,t.[Object] ,t.[Field] ,t.[Value] ;
