sql-select.sql-select-text:
SET NOCOUNT ON; SET DEADLOCK_PRIORITY LOW;
SELECT t.[name] AS [NodeUName] ,t.[dbid] AS [NodeUId] ,t.[enabled] AS [NodeEnabled] ,t.[icon] AS [NodeUIcon] ,t.[fontcolor] AS [NodeFontColor] ,t.[fontstyle] AS [NodeFontStyle] ,t.[name] AS [DatabaseName] FROM ( SELECT tSD.[name] AS [name] ,tSD.[dbid] AS [dbid] ,1 AS [enabled] ,N'TempdbIcon' AS [icon] ,N'#808080' AS [fontcolor] ,N'italic' AS [fontstyle] ,1 AS [IsSystemDatabase] FROM [master].[dbo].[sysdatabases] tSD WHERE ISNULL(@IsIncludeTempdb, 1) = 1 AND tSD.[name] IN ( N'tempdb' )
UNION ALL
SELECT tSD.[name] AS [name] ,tSD.[dbid] AS [dbid] ,1 AS [enabled] ,N'SystemDatabaseIcon' AS [icon] ,N'#080808' AS [fontcolor] ,N'normal' AS [fontstyle] ,1 AS [IsSystemDatabase] FROM [master].[dbo].[sysdatabases] tSD WHERE ISNULL(@IsIncludeSystemDatabases, 1) = 1 AND tSD.[name] IN ( N'master' ,N'model' ,N'msdb' ,N'distribution' )
UNION ALL
SELECT tSD.[name] AS [name] ,tSD.[dbid] AS [dbid] ,1 AS [enabled] ,CASE WHEN DatabasePropertyEx(tSD.[name], N'Status') = N'RESTORING' THEN N'UserDatabaseRestoringIcon' WHEN DatabasePropertyEx(tSD.[name], N'Status') = N'OFFLINE' THEN N'UserDatabaseOfflineIcon' WHEN DatabasePropertyEx(tSD.[name], N'IsInStandBy') = 1 THEN N'UserDatabaseInStandbyIcon' WHEN DatabasePropertyEx(tSD.[name], N'Updateability') = N'READ_ONLY' THEN N'UserDatabaseReadOnlyIcon' ELSE N'UserDatabaseIcon' END AS [icon] ,N'#080808' AS [fontcolor] ,N'normal' AS [fontstyle] ,0 AS [IsSystemDatabase] FROM [master].[dbo].[sysdatabases] tSD WHERE ISNULL(@IsIncludeUserDatabases, 1) = 1 AND tSD.[name] NOT IN ( N'master' ,N'model' ,N'msdb' ,N'tempdb' ,N'distribution' ) ) t WHERE DatabasePropertyEx(t.[name], N'Status') = N'ONLINE' AND t.[name] = ISNULL(@DatabaseName, t.[name]) GROUP BY t.[name] ,t.[dbid] ,t.[enabled] ,t.[icon] ,t.[fontcolor] ,t.[fontstyle] ,t.[IsSystemDatabase] ORDER BY t.[IsSystemDatabase] DESC ,t.[name] ASC ;
