SET NOCOUNT ON; SET DEADLOCK_PRIORITY LOW;
DECLARE @DatabaseName [NVARCHAR](128) ,@strSQL [NVARCHAR](4000);
CREATE TABLE #tblGuests ( [UserName] [NVARCHAR](128) NULL ,[UserID] [VARBINARY](255) NULL ,[HasDbAccess] [INTEGER] NULL ,[CreateDate] [DATETIME] NULL ,[UpdateDate] [DATETIME] NULL );
DECLARE @tblDatabaseGuests TABLE ( [DatabaseName] [NVARCHAR](128) NULL ,[UserName] [NVARCHAR](128) NULL ,[UserID] [VARBINARY](255) NULL ,[HasDbAccess] [INTEGER] NULL ,[CreateDate] [DATETIME] NULL ,[UpdateDate] [DATETIME] NULL );
SELECT @DatabaseName = MIN(tSD.[name]) FROM [master].[dbo].[sysdatabases] tSD WHERE tSD.[name] NOT IN ( N'tempdb' ) AND DatabasePropertyEx(tSD.[name], N'Status') IN ( N'ONLINE' );
WHILE (@DatabaseName IS NOT NULL) BEGIN SET @strSQL = N'USE ' + QuoteName(@DatabaseName, N']') + N';SELECT' + N' tSU.[name]' + N' ,tSU.[sid]' + N' ,tSU.[hasdbaccess]' + N' ,tSU.[createdate]' + N' ,tSU.[updatedate]' + N' FROM' + N' [dbo].[sysusers] tSU' + N' WHERE' + N' tSU.[name] = N''guest''' + N' AND tSU.[issqluser] = 1' + N' AND tSU.[hasdbaccess] = 1;';
DELETE FROM #tblGuests;
INSERT #tblGuests ( [UserName] ,[UserID] ,[HasDbAccess] ,[CreateDate] ,[UpdateDate] ) EXECUTE [sp_executesql] @strSQL;
INSERT @tblDatabaseGuests ( [DatabaseName] ,[UserName] ,[UserID] ,[HasDbAccess] ,[CreateDate] ,[UpdateDate] ) SELECT @DatabaseName ,t.[UserName] ,t.[UserID] ,t.[HasDbAccess] ,t.[CreateDate] ,t.[UpdateDate] FROM #tblGuests t;
SELECT @DatabaseName = MIN(tSD.[name]) FROM [master].[dbo].[sysdatabases] tSD WHERE tSD.[name] NOT IN ( N'tempdb' ) AND DatabasePropertyEx(tSD.[name], N'Status') IN ( N'ONLINE' ) AND tSD.[name] > @DatabaseName; END
SELECT t.[DatabaseName] ,t.[UserName] ,t.[UserID] ,t.[HasDbAccess] ,t.[CreateDate] ,t.[UpdateDate] FROM @tblDatabaseGuests t;

SET NOCOUNT ON; SET DEADLOCK_PRIORITY LOW;
DECLARE @DatabaseName [NVARCHAR](128) ,@strSQL [NVARCHAR](4000);
CREATE TABLE #tblGuests ( [UserName] [NVARCHAR](128) NULL ,[UserID] [VARBINARY](255) NULL ,[HasDbAccess] [INTEGER] NULL ,[CreateDate] [DATETIME] NULL ,[UpdateDate] [DATETIME] NULL );
DECLARE @tblDatabaseGuests TABLE ( [DatabaseName] [NVARCHAR](128) NULL ,[UserName] [NVARCHAR](128) NULL ,[UserID] [VARBINARY](255) NULL ,[HasDbAccess] [INTEGER] NULL ,[CreateDate] [DATETIME] NULL ,[UpdateDate] [DATETIME] NULL );
SELECT @DatabaseName = MIN(tSD.[name]) FROM [master].[sys].[sysdatabases] tSD WHERE tSD.[name] NOT IN ( N'tempdb' ) AND DatabasePropertyEx(tSD.[name], N'Status') IN ( N'ONLINE' );
WHILE (@DatabaseName IS NOT NULL) BEGIN SET @strSQL = N'USE ' + QuoteName(@DatabaseName, N']') + N'; SELECT tSU.[name] ,tSU.[sid] ,tSU.[hasdbaccess] ,tSU.[createdate] ,tSU.[updatedate] FROM [sys].[sysusers] tSU WHERE tSU.[name] = N''guest'' AND tSU.[issqluser] = 1 AND tSU.[hasdbaccess] = 1; ';
DELETE FROM #tblGuests;
BEGIN TRY INSERT #tblGuests ( [UserName] ,[UserID] ,[HasDbAccess] ,[CreateDate] ,[UpdateDate] ) EXECUTE [sp_executesql] @strSQL; END TRY BEGIN CATCH INSERT #tblGuests ( [UserName] ,[UserID] ,[HasDbAccess] ,[CreateDate] ,[UpdateDate] ) VALUES ( N'?' ,-1 ,0 ,N'19000101' ,N'19000101' ); END CATCH
INSERT @tblDatabaseGuests ( [DatabaseName] ,[UserName] ,[UserID] ,[HasDbAccess] ,[CreateDate] ,[UpdateDate] ) SELECT @DatabaseName ,t.[UserName] ,t.[UserID] ,t.[HasDbAccess] ,t.[CreateDate] ,t.[UpdateDate] FROM #tblGuests t;
SELECT @DatabaseName = MIN(tSD.[name]) FROM [master].[sys].[sysdatabases] tSD WHERE tSD.[name] NOT IN ( N'tempdb' ) AND DatabasePropertyEx(tSD.[name], N'Status') IN ( N'ONLINE' ) AND tSD.[name] > @DatabaseName; END
SELECT t.[DatabaseName] ,t.[UserName] ,t.[UserID] ,t.[HasDbAccess] ,t.[CreateDate] ,t.[UpdateDate] FROM @tblDatabaseGuests t;
