sql-select.sql-select-text:
SET NOCOUNT ON; SET DEADLOCK_PRIORITY LOW;
SELECT COUNT(*) FROM [sys].[objects] tSO INNER JOIN [sys].[schemas] tSS ON tSS.[schema_id] = tSO.[schema_id] WHERE tSO.[name] = N'syspublications' AND tSS.[name] = N'dbo' ;
SET NOCOUNT ON; SET DEADLOCK_PRIORITY LOW;
SELECT tSP.[name] AS [PublisherName] ,tSA.[name] AS [PublicationArticle] ,tSSS.[srvname] AS [SubscriberServerName] ,tSSS.[dest_db] AS [SubscriberDatabaseName] FROM [dbo].[syspublications] tSP LEFT OUTER JOIN [dbo].[sysarticles] tSA ON tSP.[pubid] = tSA.[pubid] LEFT OUTER JOIN [dbo].[syssubscriptions] tSSS ON tSA.[artid] = tSSS.[artid] AND tSSS.[srvid] NOT IN (-1) ORDER BY tSP.[name] ,tSA.[name] ,tSSS.[srvname] ;
