sql-select.sql-select-text:
SET NOCOUNT ON; SET DEADLOCK_PRIORITY LOW;
SELECT tSS.[name] AS [SchemaName] ,tSO.[name] AS [ObjectName] ,tSO.[type_desc] AS [ObjectType] ,tSO.[create_date] AS [ObjectCreateDate] ,tSC.[name] AS [ColumnName] FROM [sys].[objects] tSO INNER JOIN [sys].[schemas] tSS ON tSS.[schema_id] = tSO.[schema_id] INNER JOIN [sys].[columns] tSC ON tSC.[object_id] = tSO.[object_id] LEFT OUTER JOIN ( SELECT tSFK.[parent_object_id] AS [object_id] ,tSFKC.[parent_column_id] AS [column_id] FROM [sys].[foreign_keys] tSFK INNER JOIN [sys].[foreign_key_columns] tSFKC ON tSFKC.[parent_object_id] = tSFK.[parent_object_id] AND tSFKC.[constraint_object_id] = tSFK.[object_id] WHERE tSFK.[type_desc] = N'FOREIGN_KEY_CONSTRAINT'
UNION ALL
SELECT tSFK.[referenced_object_id] AS [object_id] ,tSFKC.[referenced_column_id] AS [column_id] FROM [sys].[foreign_keys] tSFK INNER JOIN [sys].[foreign_key_columns] tSFKC ON tSFKC.[referenced_object_id] = tSFK.[parent_object_id] AND tSFKC.[constraint_object_id] = tSFK.[object_id] WHERE tSFK.[type_desc] = N'FOREIGN_KEY_CONSTRAINT'
) tSFK ON tSFK.[object_id] = tSO.[object_id] AND tSFK.[column_id] = tSC.[column_id] WHERE tSO.[type_desc] = N'USER_TABLE' AND OBJECTPROPERTY(tSO.[object_id], N'IsMSShipped') = 0 AND ( tSC.[name] LIKE '%id' OR tSC.[name] LIKE '%[_]id' OR tSC.[name] LIKE 'id%' OR tSC.[name] LIKE '[_]id%' OR tSC.[name] LIKE '%[_]id[_]%' ) AND tSFK.[object_id] IS NULL GROUP BY tSS.[name] ,tSO.[name] ,tSO.[type_desc] ,tSO.[create_date] ,tSC.[name] ORDER BY tSS.[name] ,tSO.[name] ,tSC.[name] ;
