sql-select.sql-select-text:
SET NOCOUNT ON; SET DEADLOCK_PRIORITY LOW;
DECLARE @strAcceptableSymbols [NVARCHAR](256);
SET @strAcceptableSymbols = N'_$';
SELECT tSU.[name] AS [SchemaName] ,tSO.[name] AS [ObjectName] ,tSO.[xtype] AS [ObjectType] ,tSO.[crdate] AS [ObjectCreateDate] FROM [dbo].[sysobjects] tSO INNER JOIN [dbo].[sysusers] tSU ON tSU.[uid] = tSO.[uid] WHERE tSO.[xtype] IN ( N'U' ,N'V' ) AND OBJECTPROPERTY(tSO.[id], N'IsMSShipped') = 0 AND tSO.[name] LIKE N'%[^a-z' + @strAcceptableSymbols + N']%' ORDER BY tSU.[name] ,tSO.[name] ;

SET NOCOUNT ON; SET DEADLOCK_PRIORITY LOW;
DECLARE @strAcceptableSymbols [NVARCHAR](256);
SET @strAcceptableSymbols = N'_$';
SELECT tSS.[name] AS [SchemaName] ,tSO.[name] AS [ObjectName] ,tSO.[type_desc] AS [ObjectType] ,tSO.[create_date] AS [ObjectCreateDate] FROM [sys].[objects] tSO INNER JOIN [sys].[schemas] tSS ON tSS.[schema_id] = tSO.[schema_id] WHERE tSO.[type_desc] IN ( N'USER_TABLE' ,N'VIEW' ) AND OBJECTPROPERTY(tSO.[object_id], N'IsMSShipped') = 0 AND tSO.[name] LIKE N'%[^a-z' + @strAcceptableSymbols + N']%' ORDER BY tSS.[name] ,tSO.[name] ;
