sql-select.sql-select-text:
SET NOCOUNT ON; SET DEADLOCK_PRIORITY LOW;
SELECT tSS.[name] AS [SchemaName] ,tSO.[name] AS [ObjectName] ,tSO.[type_desc] AS [ObjectType] ,tSO.[create_date] AS [ObjectCreateDate] ,tSC.[name] AS [ObjectConstraintColumnName] ,tSCC.[name] AS [ConstraintName] ,tSCC.[type_desc] AS [ConstraintType] ,tSCC.[definition] AS [ConstraintDefinition] FROM ( SELECT tSCCo.[schema_id] AS [schema_id] ,tSCCo.[parent_object_id] AS [parent_object_id] ,tSCCo.[parent_column_id] AS [parent_column_id] ,tSCCo.[name] AS [name] ,tSCCo.[type_desc] AS [type_desc] ,tSCCo.[definition] AS [definition] FROM [sys].[check_constraints] tSCCo
UNION ALL
SELECT tSDCo.[schema_id] AS [schema_id] ,tSDCo.[parent_object_id] AS [parent_object_id] ,tSDCo.[parent_column_id] AS [parent_column_id] ,tSDCo.[name] AS [name] ,tSDCo.[type_desc] AS [type_desc] ,tSDCo.[definition] AS [definition] FROM [sys].[default_constraints] tSDCo ) tSCC INNER JOIN [sys].[schemas] tSS ON tSS.[schema_id] = tSCC.[schema_id] INNER JOIN [sys].[objects] tSO ON tSO.[object_id] = tSCC.[parent_object_id] INNER JOIN [sys].[columns] tSC ON tSC.[object_id] = tSO.[object_id] AND tSC.[column_id] = tSCC.[parent_column_id] WHERE tSO.[type_desc] = N'USER_TABLE' AND OBJECTPROPERTY(tSO.[object_id], N'IsMSShipped') = 0 AND tSCC.[name] LIKE N'%[_][0-9a-f][0-9a-f][0-9a-f][0-9a-f][0-9a-f][0-9a-f][0-9a-f][0-9a-f]' GROUP BY tSS.[name] ,tSO.[name] ,tSO.[type_desc] ,tSO.[create_date] ,tSC.[name] ,tSCC.[name] ,tSCC.[type_desc] ,tSCC.[definition] ORDER BY tSS.[name] ,tSO.[name] ,tSC.[name] ;
