sql-select.sql-select-text:
SET NOCOUNT ON; SET DEADLOCK_PRIORITY LOW;
DECLARE @MasterCompatibilityLevel [INTEGER] ,@MasterCollation [NVARCHAR](128) ;
SELECT TOP 1 @MasterCompatibilityLevel = tSD.[cmptlevel] ,@MasterCollation = CONVERT([NVARCHAR](128), DatabasePropertyEx(tSD.[name], N'Collation')) FROM [master].[dbo].[sysdatabases] tSD WHERE tSD.[name] = N'master';
SELECT tSD.[name] AS [DatabaseName] ,tSD.[cmptlevel] AS [DatabaseCompatibilityLevel] ,@MasterCompatibilityLevel AS [MasterCompatibilityLevel] ,suser_sname(tSD.[sid]) AS [DatabaseOwner] ,tSD.[crdate] AS [DatabaseCreationDate] ,DatabasePropertyEx(tSD.[name], N'Collation') AS [DatabaseCollation] ,@MasterCollation AS [MasterCollation] ,DatabasePropertyEx(tSD.[name], N'Status') AS [DatabaseStatus] ,DatabasePropertyEx(tSD.[name], N'Recovery') AS [DatabaseRecovery] ,DatabasePropertyEx(tSD.[name], N'Updateability') AS [DatabaseUpdateability] ,DatabasePropertyEx(tSD.[name], N'IsInStandBy') AS [DatabaseIsInStandBy] ,DatabasePropertyEx(tSD.[name], N'IsAutoClose') AS [DatabaseIsAutoClose] ,DatabasePropertyEx(tSD.[name], N'IsAutoShrink') AS [DatabaseIsAutoShrink] ,DatabasePropertyEx(tSD.[name], N'IsAutoCreateStatistics') AS [DatabaseIsAutoCreateStatistics] ,DatabasePropertyEx(tSD.[name], N'IsAutoUpdateStatistics') AS [DatabaseIsAutoUpdateStatistics] ,DatabasePropertyEx(tSD.[name], N'IsPublished') AS [DatabaseIsPublished] ,DatabasePropertyEx(tSD.[name], N'IsSubscribed') AS [DatabaseIsSubscribed] FROM [master].[dbo].[sysdatabases] tSD ORDER BY tSD.[name];

SET NOCOUNT ON; SET DEADLOCK_PRIORITY LOW;
DECLARE @MasterCompatibilityLevel [INTEGER] ,@MasterCollation [NVARCHAR](128) ;
SELECT TOP 1 @MasterCompatibilityLevel = tSD.[compatibility_level] ,@MasterCollation = CONVERT([NVARCHAR](128), DatabasePropertyEx(tSD.[name], N'Collation')) FROM [master].[sys].[databases] tSD WHERE tSD.[name] = N'master';
SELECT tSD.[name] AS [DatabaseName] ,tSD.[compatibility_level] AS [DatabaseCompatibilityLevel] ,@MasterCompatibilityLevel AS [MasterCompatibilityLevel] ,suser_sname(tSD.[owner_sid]) AS [DatabaseOwner] ,tSD.[create_date] AS [DatabaseCreationDate] ,DatabasePropertyEx(tSD.[name], N'Collation') AS [DatabaseCollation] ,@MasterCollation AS [MasterCollation] ,DatabasePropertyEx(tSD.[name], N'Status') AS [DatabaseStatus] ,DatabasePropertyEx(tSD.[name], N'Recovery') AS [DatabaseRecovery] ,DatabasePropertyEx(tSD.[name], N'Updateability') AS [DatabaseUpdateability] ,DatabasePropertyEx(tSD.[name], N'IsInStandBy') AS [DatabaseIsInStandBy] ,DatabasePropertyEx(tSD.[name], N'IsAutoClose') AS [DatabaseIsAutoClose] ,DatabasePropertyEx(tSD.[name], N'IsAutoShrink') AS [DatabaseIsAutoShrink] ,DatabasePropertyEx(tSD.[name], N'IsAutoCreateStatistics') AS [DatabaseIsAutoCreateStatistics] ,DatabasePropertyEx(tSD.[name], N'IsAutoUpdateStatistics') AS [DatabaseIsAutoUpdateStatistics] ,DatabasePropertyEx(tSD.[name], N'IsPublished') AS [DatabaseIsPublished] ,DatabasePropertyEx(tSD.[name], N'IsSubscribed') AS [DatabaseIsSubscribed] FROM [master].[sys].[databases] tSD ORDER BY tSD.[name];
