SET NOCOUNT ON; SET DEADLOCK_PRIORITY LOW;
DECLARE @intMinSpId [INTEGER] ,@intMaxSpId [INTEGER] ,@intMySpId [INTEGER];
SET @intMinSpId = 0; SET @intMaxSpId = 0; SET @intMySpId = @@spid;
IF (@boolIsShowSystemProcesses IS NOT NULL) BEGIN IF (ISNULL(@boolIsShowSystemProcesses, 0) = 1) BEGIN SET @intMinSpId = 1; END ELSE BEGIN SET @intMinSpId = 51; END END ELSE BEGIN SET @intMinSpId = 51; END
IF (@boolIsShowUserProcesses IS NOT NULL) BEGIN IF (ISNULL(@boolIsShowUserProcesses, 0) = 1) BEGIN SET @intMaxSpId = 999; END ELSE BEGIN SET @intMaxSpId = 50; END END ELSE BEGIN SET @intMaxSpId = 50; END
SELECT tSP.[spid] AS [SpID] ,tSP.[blocked] AS [BlockedID] ,RTRIM(tSP.[program_name]) AS [ProgramName] ,RTRIM(tSP.[cmd]) AS [Command] ,RTRIM(tSP.[loginame]) AS [LogiName] ,tSP.[last_batch] AS [LastBatchDateTime] ,tSP.[login_time] AS [LoginDateTime] FROM [master].[dbo].[sysprocesses] tSP WHERE tSP.[spid] NOT IN (@intMySpId) AND tSP.[spid] >= @intMinSpId AND tSP.[spid] <= @intMaxSpId AND ( ISNULL(@boolIsShowLockedProcessesOnly, 0) = 0 OR ( tSP.[blocked] IS NOT NULL AND tSP.[blocked] NOT IN (0) ) ) ORDER BY tSP.[spid] ASC;

