sql-select.sql-select-text:
SET NOCOUNT ON; SET DEADLOCK_PRIORITY LOW;
SELECT COUNT(*) FROM [sys].[objects] tSO INNER JOIN [sys].[schemas] tSS ON tSS.[schema_id] = tSO.[schema_id] WHERE tSO.[name] = N'MSreplication_subscriptions' AND tSS.[name] = N'dbo' ;
SET NOCOUNT ON; SET DEADLOCK_PRIORITY LOW;
SELECT tRS.[publisher] AS [PublisherName] ,tRS.[publisher_db] AS [PublisherDatabaseName] ,tRS.[publication] AS [PublisherPublication] FROM [dbo].[MSreplication_subscriptions] tRS ORDER BY tRS.[publisher] ,tRS.[publisher_db] ,tRS.[publication] ;
