sql-select.sql-select-text:
SET NOCOUNT ON; SET DEADLOCK_PRIORITY LOW;
DECLARE @dbid [INTEGER] ;
SET @dbid = db_id();
SELECT tSS.[name] AS [SchemaName] ,tSO.[name] AS [ObjectName] ,tSO.[type_desc] AS [ObjectType] ,tSO.[create_date] AS [ObjectCreateDate] ,tSI.[name] AS [IndexName] ,d.[index_type_desc] AS [IndexType] ,d.[page_count] AS [IndexPages] ,d.[avg_fragmentation_in_percent] AS [IndexFragmentation] ,d.[fragment_count] AS [IndexFragmentCount] ,d.[avg_fragment_size_in_pages] AS [IndexAvgFragmentSizeInPages] FROM [sys].[dm_db_index_physical_stats] (@dbid, NULL, NULL , NULL, N'LIMITED') d INNER JOIN [sys].[indexes] tSI ON tSI.[object_id] = d.[object_id] AND tSI.[index_id] = d.[index_id] INNER JOIN [sys].[objects] tSO ON tSO.[object_id] = d.[object_id] INNER JOIN [sys].[schemas] tSS ON tSS.[schema_id] = tSO.[schema_id] WHERE d.[avg_fragmentation_in_percent] >= ISNULL(@isMinFragmentationInPercent, d.[avg_fragmentation_in_percent]) AND d.[avg_fragmentation_in_percent] <= ISNULL(@isMaxFragmentationInPercent, d.[avg_fragmentation_in_percent]) AND d.[page_count] >= ISNULL(@isMinPageCountToShow, 0) ORDER BY tSS.[name] ,tSO.[name] ,tSI.[name];
